$(function(){$(".delete").click(function(){Swal.fire({title:"Czy na pewno chcesz usunąć rekord?",icon:"warning",showCancelButton:!0,confirmButtonText:"Tak",cancelButtonText:"Nie"}).then(e=>{e.value&&$.ajax({method:"DELETE",url:window.location.origin+window.location.pathname+"/"+$(this).data("id")}).done(function(n){window.location.reload()}).fail(function(n){Swal.fire("Oops...",n.responseJSON.message,n.responseJSON.status)})})})});
