$(function(){$(".delete").click(function(){Swal.fire({title:"Czy na pewno chcesz usunąć rekord?",icon:"warning",showCancelButton:!0,confirmButtonText:"Tak",cancelButtonText:"Nie"}).then(n=>{n.value&&$.ajax({method:"DELETE",url:"/users/"+$(this).data("id")}).done(function(e){window.location.reload()}).fail(function(e){Swal.fire("Oops...",e.responseJSON.message,e.responseJSON.status)})})})});
